import React from 'react';
import ReactDOM from 'react-dom';
import Board from './Board';
import Buttons from './Buttons';
import ProgressBars from './ProgressBars';

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      mathset: [
        {
         buttons: Array(4).fill(1,2,3,4),
         progress: 1
        },
        {
         buttons: Array(4).fill(-1,0,1,2),
         progress: 2
        },
        {
         buttons: Array(4).fill(3,2,1,0),
         progress: 3
        },
        {
         buttons: Array(4).fill(1,1,1,1),
         progress: 100
        }
      ]
    };
  }

  handleClick(index,i) {
    this.state.mathset[index].progress = this.state.mathset[index].progress+this.state.mathset[index].buttons[i];
  }


  render() {
    return (
      <div className="math">
        <div className="set0">
        <ProgressBars progress={this.state.mathset[0].progress} />
          <Buttons
          buttons={this.state.mathset[0].buttons}
          onClick={i => this.handleClick(0,i)}
          />
        </div>
           <div className="set1">
        <ProgressBars progress={this.state.mathset[1].progress} />
          <Buttons
          buttons={this.state.mathset[1].buttons}
          onClick={i => this.handleClick(1,i)}
          />
           </div>
          <div className="set2">
        <ProgressBars progress={this.state.mathset[2].progress} />
          <Buttons
          buttons={this.state.mathset[2].buttons}
          onClick={i => this.handleClick(2,i)}
          />
           </div>
        <div className="set3">
        <ProgressBars progress={this.state.mathset[3].progress} />
          <Buttons
          buttons={this.state.mathset[3].buttons}
          onClick={i => this.handleClick(3,i)}
          />
           </div>
        </div>

    );
  }
}



export default Main;
