import React from 'react';
import ReactDOM from 'react-dom';
import Board from './Board';
import Buttons from './Buttons';
import ProgressBars from './ProgressBars';
import Container from 'react-bootstrap/Container';

class Main extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      buttons:null,
      bars:null,
      limit:0,
      currentBar:0,
      mathset: [
        {
         buttons: [1,2,3,4],
         progress: 1
        },
        {
         buttons: [-1,0,1,2],
         progress: 2
        },
        {
         buttons: [3,2,1,0],
         progress: 3
        },
        {
         buttons: [1,1,1,1],
         progress: 100
        }
      ]
    };
  }
componentDidMount(){

   fetch('http://pb-api.herokuapp.com/bars')
      .then(response => response.json())
      .then(data => this.setState(
        { buttons:data.buttons,
          bars: data.bars,
          limit: data.limit
         }));

  }

  handleClick(index,i) {
    const current=this.state.mathset;
    current[index].progress = current[index].progress+this.state.mathset[index].buttons[i];
   this.setState({
     mathset:current
    });
  }

createResult(){

  var len=this.state.buttons.length();
      for (let i=0; i<len ; i++){
      <Buttons
          buttons={this.state.buttons[i]}
          onClick={j => this.handleClick(0,j)}
          />
      }
      <Tabs defaultActiveKey="0" onSelect={key => this.setState({ currentBar:key })}>

      this.state.bars.map((i,bar)=>{

   <Tab eventKey={""+i} title={"bar"+i} >
     <ProgressBars 
      progress={bar} 
      default={this.state.limit} />
  </Tab>

  })
      </Tabs>

}
  render() {
    return (

      <div className="math">

        <div className="set0">
          <Container>
        <ProgressBars progress={this.state.mathset[0].progress} />
          <Buttons
          buttons={this.state.mathset[0].buttons}
          onClick={i => this.handleClick(0,i)}
          />
           </Container>
        </div>
           <div className="set1">
            <Container>
        <ProgressBars progress={this.state.mathset[1].progress} />
          <Buttons
          buttons={this.state.mathset[1].buttons}
          onClick={i => this.handleClick(1,i)}
          />
           </Container>
           </div>
          <div className="set2">
           <Container>
        <ProgressBars progress={this.state.mathset[2].progress} />
          <Buttons
          buttons={this.state.mathset[2].buttons}
          onClick={i => this.handleClick(2,i)}
          />
           </Container>
           </div>
        <div className="set3">
         <Container>
        <ProgressBars progress={this.state.mathset[3].progress} />
          <Buttons
          buttons={this.state.mathset[3].buttons}
          onClick={i => this.handleClick(3,i)}
          />
           </Container>
           </div>
          <div>
          <Container>
            {this.createResult()}
          </Container>
          </div>
        </div>

    );
  }
}



export default Main;
